<script type="text/javascript">
  function billingAddressPanelObject(){
    // divs
    this.divStateDropDown = $("#ContentRegion1_ctl00_billingAddressPanel_divStateDropDown");
    this.divProvinceDropDown = $("#ContentRegion1_ctl00_billingAddressPanel_divProvinceDropDown");
    this.divRegionTextBox = $("#ContentRegion1_ctl00_billingAddressPanel_divRegionTextBox");
    this.divAddressPanel = $("#ContentRegion1_ctl00_billingAddressPanel_divAddressPanel");
    this.divZip = $("#ContentRegion1_ctl00_billingAddressPanel_divZip");
    this.divZip2 = $("#ContentRegion1_ctl00_billingAddressPanel_divZip2");

    // form fields
    this.ddlCountry = $("#ContentRegion1_ctl00_billingAddressPanel_ddlCountry");
    this.txtAddress1 = $("#ContentRegion1_ctl00_billingAddressPanel_txtAddress1");
    this.txtAddress2 = $("#ContentRegion1_ctl00_billingAddressPanel_txtAddress2");
    this.txtCity = $("#ContentRegion1_ctl00_billingAddressPanel_txtCity");
    this.txtZip = $("#ContentRegion1_ctl00_billingAddressPanel_txtZip");
    this.txtZip2 = $("#ContentRegion1_ctl00_billingAddressPanel_txtZip2");
    this.ddlState = $("#ContentRegion1_ctl00_billingAddressPanel_ddlState");
    this.ddlProvince = $("#ContentRegion1_ctl00_billingAddressPanel_ddlProvince");
    this.txtRegion = $("#ContentRegion1_ctl00_billingAddressPanel_txtRegion");
    this.hRequiresValidation = $("#ContentRegion1_ctl00_billingAddressPanel_hRequiresValidation");

    // validators
    this.cvCountry = $("#ContentRegion1_ctl00_billingAddressPanel_cvCountry");
    this.cvAddress1 = $("#ContentRegion1_ctl00_billingAddressPanel_cvAddress1");
    this.cvCity = $("#ContentRegion1_ctl00_billingAddressPanel_cvCity");
    this.cvState = $("#ContentRegion1_ctl00_billingAddressPanel_cvState");
    this.cvProvince = $("#ContentRegion1_ctl00_billingAddressPanel_cvProvince");
    this.cvZip = $("#ContentRegion1_ctl00_billingAddressPanel_cvZip");
    this.cvZip2 = $("#ContentRegion1_ctl00_billingAddressPanel_cvZip2");

    this.showNumberTextboxes = false;

    this.setRequiredFieldMarkersVisibility = function () {     
      if(!this.requiresValidation()){
        this.divAddressPanel.find("span.required").removeClass('required');
      }
      else{
        if(this.ddlCountry.val() != "US" && this.ddlCountry.val() != "CA"){
          $('#ContentRegion1_ctl00_billingAddressPanel_spanRequiredZip').removeClass('required');
          ValidatorEnable(this.cvZip[0], true);

          $('#ContentRegion1_ctl00_billingAddressPanel_spanRequiredZip2').removeClass('required');
          ValidatorEnable(this.cvZip2[0], true);
        }
        else{
          $('#ContentRegion1_ctl00_billingAddressPanel_spanRequiredZip').addClass('required');
          $('#ContentRegion1_ctl00_billingAddressPanel_spanRequiredZip2').addClass('required');
        }
      }
    };

    this.requiresValidation = function () {
      return this.hRequiresValidation.val() == "true";
    };

    this.setAddressPanelVisibility = function () {
      // zip code field (only use numeric zip control for US). 
      // use text zip code field for all other countries.
      if (this.ddlCountry.val() == "US"){
        showElement(this.divZip);
        hideElement(this.divZip2);
      }
      else{
        hideElement(this.divZip);
        showElement(this.divZip2);
      }    

      // all other fields
      if (this.ddlCountry.val() == "US") {
        hideElement(this.divProvinceDropDown);
        hideElement(this.divRegionTextBox);
        showElement(this.divStateDropDown);
      }
      else if (this.ddlCountry.val() == "CA") {        
        hideElement(this.divStateDropDown);
        hideElement(this.divRegionTextBox);
        showElement(this.divProvinceDropDown);
      }
      else if (this.ddlCountry.val() != "") {
        hideElement(this.divStateDropDown);
        hideElement(this.divProvinceDropDown);
        showElement(this.divRegionTextBox);
      }
      else {
        hideElement(this.divProvinceDropDown);
        hideElement(this.divRegionTextBox);
        showElement(this.divStateDropDown);
      }
    };

    this.getStateProvinceRegion = function () {
      if (this.ddlCountry.val() == "US") {
        return this.ddlState.val();
      }
      else if (this.ddlCountry.val() == "CA") {
        return this.ddlProvince.val();
      }
      else {
        return this.txtRegion.val();
      }
    };

    this.setStateProvinceRegion = function (val) {
      if (this.ddlCountry.val() == "US") {
        this.ddlState.val(val);
      }
      else if (this.ddlCountry.val() == "CA") {
        this.ddlProvince.val(val);
      }
      else {
        this.txtRegion.val(val);
      }
    };

    this.getZip = function () {
      if(!this.txtZip.is(":disabled")){
        return this.txtZip.val();
      }
      else {
        return this.txtZip2.val();
      }      
    };

    this.setZip = function (val) {
      this.txtZip.val(val);
      this.txtZip2.val(val);
    };

    // client validation functions
    this.cvCountry_ClientValidate = function(source, args){
      var requiresValidation = !this.ddlCountry.is(":disabled") && this.requiresValidation();

      if(requiresValidation)
        return this.ddlCountry.val() != "";
      else
        return true;
    };

    this.cvState_ClientValidate = function(source, args){
      var requiresValidation = !this.ddlState.is(":disabled") && this.requiresValidation();

      if(requiresValidation)
        return this.ddlCountry.val() != "US" || this.ddlState.val() != "";
      else
        return true;
    };

    this.cvProvince_ClientValidate = function(source, args){
      var requiresValidation = !this.ddlProvince.is(":disabled") && this.requiresValidation();

      if(requiresValidation)
        return this.ddlCountry.val() != "CA" || this.ddlProvince.val() != "";
      else
        return true;
    };

    this.cvAddress1_ClientValidate = function(source, args){
      var requiresValidation = !this.txtAddress1.is(":disabled") && this.requiresValidation();

      if(requiresValidation)
        return this.txtAddress1.val() != "";
      else
        return true;
    };

    this.cvCity_ClientValidate = function(source, args){
      var requiresValidation = !this.txtCity.is(":disabled") && this.requiresValidation();

      if(requiresValidation)
        return this.txtCity.val() != "";
      else
        return true;
    }; 
    
    this.cvZip_ClientValidate = function(source, args){
      var requiresValidation = !this.txtZip.is(":disabled") && this.requiresValidation();

      if(requiresValidation){
        var us_zipcode_regex = /^\d{5}(?:[-\s]?\d{4})?$/;
        var zipVal = this.txtZip.val();
        var isValid;

        if(this.ddlCountry.val() == "US"){
            isValid = us_zipcode_regex.test(zipVal);
        }
        else if(this.ddlCountry.val() == "CA"){
            isValid = $.trim(zipVal) != "";
        }
        else{
            isValid = true;
        }

        return isValid;
      }
      else{
        return true;
      }
    }; 
    this.cvZip2_ClientValidate = function(source, args){
      var requiresValidation = !this.txtZip2.is(":disabled") && this.requiresValidation();

      if(requiresValidation){
        var us_zipcode_regex = /^\d{5}(?:[-\s]?\d{4})?$/;
        var zipVal = this.txtZip2.val();
        var isValid;

        if(this.ddlCountry.val() == "US"){
            isValid = us_zipcode_regex.test(zipVal);
        }
        else if(this.ddlCountry.val() == "CA"){
            isValid = $.trim(zipVal) != "";
        }
        else{
            isValid = true;
        }

        return isValid;
      }
      else{
        return true;
      }
    };         
  }

  var billingAddressPanel = new billingAddressPanelObject();

  $(document).ready(function () {
    // set visibility of form elements
    billingAddressPanel.setAddressPanelVisibility();

    // set visibility of required field markers
    billingAddressPanel.setRequiredFieldMarkersVisibility();

    // event handlers
    billingAddressPanel.ddlCountry.change(function () {
      if ($(this).val() != "") {
        billingAddressPanel.setAddressPanelVisibility();
        billingAddressPanel.setRequiredFieldMarkersVisibility();
      }      
    });

  });

  function billingAddressPanel_cvCountry_ClientValidate(source, args){
    args.IsValid = billingAddressPanel.cvCountry_ClientValidate(source, args);
  }

  function billingAddressPanel_cvState_ClientValidate(source, args){
    args.IsValid = billingAddressPanel.cvState_ClientValidate(source, args);
  }

  function billingAddressPanel_cvProvince_ClientValidate(source, args){
    args.IsValid = billingAddressPanel.cvProvince_ClientValidate(source, args);
  }

  function billingAddressPanel_cvAddress1_ClientValidate(source, args){
    args.IsValid = billingAddressPanel.cvAddress1_ClientValidate(source, args);
  }

  function billingAddressPanel_cvCity_ClientValidate(source, args){
    args.IsValid = billingAddressPanel.cvCity_ClientValidate(source, args);
  }

  function billingAddressPanel_cvZip_ClientValidate(source, args){
    args.IsValid = billingAddressPanel.cvZip_ClientValidate(source, args);
  }

  function billingAddressPanel_cvZip2_ClientValidate(source, args){
    args.IsValid = billingAddressPanel.cvZip2_ClientValidate(source, args);
  }
</script>
